window.onload=()=>{let e=window.localStorage.getItem("edr-theme");const t=document.getElementById("theme-wrapper");e==="light"?t.classList.add("swap-active"):(e="dark",t.classList.remove("swap-active")),document.documentElement.setAttribute("data-theme",e),window.localStorage.setItem("edr-theme",e),t.addEventListener("click",()=>{e==="light"?(e="dark",t.classList.remove("swap-active")):(e="light",t.classList.add("swap-active")),document.documentElement.setAttribute("data-theme",e),window.localStorage.setItem("edr-theme",e)})};
